pragma solidity >0.4.23 <0.7.0;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./helper/IERC1594.sol";
import "./helper/IERC1643.sol";
import "./helper/IERC1644.sol";

/**
 * @title Implementation of ERC1400 (Subset of ERC1400 https://github.com/ethereum/EIPs/issues/1411)
 */
contract LoanCoin is IERC20, IERC1594, IERC1643, IERC1644 {
  using SafeMath for uint256;

  constructor(string memory name, string memory symbol) public {
    _name = name;
    _symbol = symbol;
    _totalSupply = 0;
  }

  /************************************************************************************************/
  /****************************** ERC20 INTERFACE ****************************/
  /************************************************************************************************/

  // Optional
  string internal _name;
  string internal _symbol;

  /**
  * @dev Get the number of decimals of the token.
  * @return The number of decimals of the token. For retrocompatibility, decimals are forced to 18 in ERC1400.
  */
  function decimals() external pure returns(uint8) {
    return uint8(18);
  }

  // Compulsory

  mapping (address => uint256) internal _balances;

  mapping (address => mapping (address => uint256)) internal _allowed;

  uint256 internal _totalSupply;

  /**
   * @dev Total number of tokens in existence
   */
  function totalSupply() external view override returns (uint256) {
      return _totalSupply;
  }

  /**
   * @dev Gets the balance of the specified address.
   * @param owner The address to query the balance of.
   * @return An uint256 representing the amount owned by the passed address.
   */
  function balanceOf(address owner) external view override returns (uint256) {
      return _balances[owner];
  }

  /**
   * @dev Transfer token for a specified address
   * @param to The address to transfer to.
   * @param value The amount to be transferred.
   * @return A boolean that indicates if the operation was successful.
   */
  function transfer(address to, uint256 value) external override returns (bool) {
      _transfer(msg.sender, msg.sender, to, value, "");
      return true;
  }

  /**
   * @dev Function to check the amount of tokens that an owner allowed to a spender.
   * @param owner address The address which owns the funds.
   * @param spender address The address which will spend the funds.
   * @return A uint256 specifying the amount of tokens still available for the spender.
   */
  function allowance(address owner, address spender) external view override returns (uint256) {
      return _allowed[owner][spender];
  }

  /**
   * @dev Approve the passed address to spend the specified amount of tokens on behalf of 'msg.sender'.
   * @param spender The address which will spend the funds.
   * @param value The amount of tokens to be spent.
   * @return A boolean that indicates if the operation was successful.
   */
  function approve(address spender, uint256 value) external override returns (bool) {
    require(spender != address(0), "56"); // 0x56	invalid sender
    _allowed[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
  }

  /**
  * @dev Transfer tokens from one address to another
  * @param from address The address which you want to send tokens from
  * @param to address The address which you want to transfer to
  * @param value uint256 the amount of tokens to be transferred
  */
  function transferFrom(address from, address to, uint256 value) external override returns (bool) {
      _transfer(msg.sender, from, to, value, "");
      return true;
  }

  function _transfer(address operator, address from, address to, uint256 value, bytes memory data) internal {
      require(to != address(0), "57"); // 0x57 invalid receiver
      // If operator == from or operator == controller, check if _balances[from] is >= value
      // If operator != from, check if _allowed[from][operator] is >= value
      if (operator != from) {
        require(value <= _allowed[from][operator], "53"); // 0x53	insufficient allowance
        _allowed[from][operator] = _allowed[from][operator].sub(value);
      }
      require(value <= _balances[from], "52"); // 0x52 insufficient balance

      _balances[from] = _balances[from].sub(value);
      _balances[to] = _balances[to].add(value);

      emit Transfer(from, to, value);
  }

  /************************************************************************************************/
  /****************************** ERC1594 INTERFACE ****************************/
  /************************************************************************************************/

  /****************************************** Transfers *******************************************/

  /**
   * @dev Transfer the amount of tokens from the address 'msg.sender' to the address 'to'.
   * @param to Token recipient.
   * @param value Number of tokens to transfer.
   * @param data Information attached to the transfer, by the token holder.
   */
  function transferWithData(address to, uint256 value, bytes calldata data) external override {
    _transfer(msg.sender, msg.sender, to, value, data);
  }

  /**
   * @dev Transfer the amount of tokens on behalf of the address 'from' to the address 'to'.
   * @param from Token holder (or 'address(0)' to set from to 'msg.sender').
   * @param to Token recipient.
   * @param value Number of tokens to transfer.
   * @param data Information attached to the transfer, and intended for the token holder ('from').
   */
  function transferFromWithData(address from, address to, uint256 value, bytes calldata data) external override {
    _transfer(msg.sender, from, to, value, data);
  }
  /************************************************************************************************/

  /*************************************** Token Redemption ***************************************/
  /**.
   * @dev Redeem the amount of tokens from the address 'msg.sender'.
   * @param value Number of tokens to redeem.
   * @param data Information attached to the redemption, by the token holder.
   */
  function redeem(uint256 value, bytes calldata data) external override {
    _redeem(msg.sender, msg.sender, value, data);
  }

  /**
   * @dev Redeem the amount of tokens on behalf of the address from.
   * @param from Token holder whose tokens will be redeemed (or address(0) to set from to msg.sender).
   * @param value Number of tokens to redeem.
   * @param data Information attached to the redemption.
   */
  function redeemFrom(address from, uint256 value, bytes calldata data) external override {
    _redeem(msg.sender, from, value, data);
  }

  function _redeem(address operator, address from, uint256 value, bytes memory data) internal {
    require(from != address(0), "56"); // 0x56 invalid sender
    if (operator != from) {
      require(value <= _allowed[from][operator], "53"); // 0x53	insufficient allowance
      _allowed[from][operator] = _allowed[from][operator].sub(value);
    }
    require(value <= _balances[from], "52"); // 0x52 insufficient balance

    _totalSupply = _totalSupply.sub(value);
    _balances[from] = _balances[from].sub(value);

    emit Redeemed(operator, from, value, data);
  }

  /**************************************** Token Issuance ****************************************/
  // Indicate whether the token can still be issued by the issuer or not anymore.
  bool internal _isIssuable;

  /**
   * @dev Know if new tokens can be issued in the future.
   * If a token returns FALSE for `isIssuable()` then it MUST never allow additional tokens to be issued.
   * @return bool 'true' if tokens can still be issued by the issuer, 'false' if they can't anymore.
   */
  function isIssuable() external view override returns (bool) {
    return _isIssuable;
  }
  /**
   * @dev Issue tokens from default partition.
   * @param tokenHolder Address for which we want to issue tokens.
   * @param value Number of tokens issued.
   * @param data Information attached to the issuance, by the issuer.
   */
  function issue(address tokenHolder, uint256 value, bytes calldata data) external override {
    _issue(msg.sender, tokenHolder, value, data);
  }

  function _issue(address operator, address to, uint256 value, bytes memory data) internal {
    require(to != address(0), "57"); // 0x57 invalid receiver

    // Insert logic about who can issue...?

    _totalSupply = _totalSupply.add(value);
    _balances[to] = _balances[to].add(value);

    emit Issued(operator, to, value, data);
  }
  /************************************************************************************************/

  /************************************************************************************************/
  /****************************** ERC1643 INTERFACE ****************************/
  /************************************************************************************************/

  /************************************* Document Management **************************************/
  struct Document {
    bytes32 docHash; // Hash of the document
    string docUri; // URI of the document that exist off-chain
  }

  // mapping to store the documents details in the document
  mapping(bytes32 => Document) internal _documents;

  /**
   * @notice Used to attach a new document to the contract, or update the URI or hash of an existing attached document
   * @dev Can only be executed by the owner of the contract.
   * @param name Name of the document. It should be unique always
   * @param documentUri Off-chain uri of the document from where it is accessible to investors/advisors to read.
   * @param documentHash hash (of the contents) of the document.
   */
  function setDocument(bytes32 name, string calldata documentUri, bytes32 documentHash) external override {
    _documents[name] = Document(documentHash, documentUri);
    emit DocumentSet(name, documentUri, documentHash);
  }

  /**
   * @notice Used to remove an existing document from the contract by giving the name of the document.
   * @dev Can only be executed by the owner of the contract.
   * @param name Name of the document. It should be unique always
   */
  function removeDocument(bytes32 name) external override {
    require(bytes(_documents[name].docUri).length != 0, "invalid name");
    delete _documents[name];
    emit DocumentRemoved(name, _documents[name].docUri, _documents[name].docHash);
  }

  /**
   * @notice Used to return the details of a document with a known name (`bytes32`).
   * @param name Name of the document
   * @return string The URI associated with the document.
   * @return bytes32 The hash (of the contents) of the document.
   */
  function getDocument(bytes32 name) external view override returns (string memory, bytes32) {
    require(bytes(_documents[name].docUri).length != 0, "invalid name");
    return (
      _documents[name].docUri,
      _documents[name].docHash
    );
  }

  /************************************************************************************************/

  /************************************************************************************************/
  /****************************** ERC1644 INTERFACE ****************************/
  /************************************************************************************************/

  /************************************* Controller Operation **************************************/

  // Address of the controller which is a delegated entity
  // set by the issuer/owner of the token
  address public controller;

  // Modifier to check whether the msg.sender is authorised or not
  modifier onlyController() {
      require(msg.sender == controller, "Not Authorised");
      _;
  }

  /**
   * @notice In order to provide transparency over whether `controllerTransfer` / `controllerRedeem` are useable
   * or not `isControllable` function will be used.
   * @dev If `isControllable` returns `false` then it always return `false` and
   * `controllerTransfer` / `controllerRedeem` will always revert.
   * @return bool `true` when controller address is non-zero otherwise return `false`.
   */
  function isControllable() external view override returns (bool) {
      return _isControllable();
  }

  /**
   * @notice Internal function to know whether the controller functionality
   * allowed or not.
   * @return bool `true` when controller address is non-zero otherwise return `false`.
   */
  function _isControllable() internal view returns (bool) {
      if (controller == address(0))
          return false;
      else
          return true;
  }

  /**
   * @notice This function allows an authorised address to transfer tokens between any two token holders.
   * The transfer must still respect the balances of the token holders (so the transfer must be for at most
   * `balanceOf(_from)` tokens) and potentially also need to respect other transfer restrictions.
   * @dev This function can only be executed by the `controller` address.
   * @param _from Address The address which you want to send tokens from
   * @param _to Address The address which you want to transfer to
   * @param _value uint256 the amount of tokens to be transferred
   * @param _data data to validate the transfer. (It is not used in this reference implementation
   * because use of `_data` parameter is implementation specific).
   * @param _operatorData data attached to the transfer by controller to emit in event. (It is more like a reason string
   * for calling this function (aka force transfer) which provides the transparency on-chain).
   */
  function controllerTransfer(address _from, address _to, uint256 _value, bytes calldata _data, bytes calldata _operatorData) external override {
      _transfer(msg.sender, _from, _to, _value, _data);
      emit ControllerTransfer(msg.sender, _from, _to, _value, _data, _operatorData);
  }

  /**
   * @notice This function allows an authorised address to redeem tokens for any token holder.
   * The redemption must still respect the balances of the token holder (so the redemption must be for at most
   * `balanceOf(_tokenHolder)` tokens) and potentially also need to respect other transfer restrictions.
   * @dev This function can only be executed by the `controller` address.
   * @param _tokenHolder The account whose tokens will be redeemed.
   * @param _value uint256 the amount of tokens need to be redeemed.
   * @param _data data to validate the transfer. (It is not used in this reference implementation
   * because use of `_data` parameter is implementation specific).
   * @param _operatorData data attached to the transfer by controller to emit in event. (It is more like a reason string
   * for calling this function (aka force transfer) which provides the transparency on-chain).
   */
  function controllerRedeem(address _tokenHolder, uint256 _value, bytes calldata _data, bytes calldata _operatorData) external override {
      _redeem(msg.sender, _tokenHolder, _value, _data);
      emit ControllerRedemption(msg.sender, _tokenHolder, _value, _data, _operatorData);
  }


}

